export default class FancyMouse{
  constuctor() {
    this.busy = false;
    this.xOffset = 0;
    this.yOffset = 0;
    this.effect = null;
  }
  setOffset(buttonX, buttonY) {
    this.xOffset = mouseX - buttonX;
    this.yOffset = mouseY - buttonY;
  }
}
