class Button {

    constructor(button_name, button_id) {
      this.button_name = button_name;
      this.button_id = button_id;

      this.x = 0;
      this.y = 0;
      this.w = textWidth(button_name) + 14;
      this.h = font_pixels + 10;
      this.fillAlpha = 100;

      this.text_x = 0;
      this.text_y = 0;
    }
    mouseInRegion() {
      return (mouseX > this.x - this.w / 2 - mouse_padding &&
        mouseX < this.x + this.w / 2 + mouse_padding &&
        mouseY > this.y - this.h / 2 - mouse_padding &&
        mouseY < this.y + this.h / 2 + mouse_padding);
    }

    resized() {
      this.x = w_padding - 150 + this.w / 2 - 7;
      this.y = map(this.button_id, 0, buttons.length, h_padding, height - h_padding);
      this.text_x = this.x - this.w / 2 + 7;
      this.text_y = this.y - 6;
    }

    run() {
      this.update();
      this.display();
    }

    update() {

      if (this.mouseInRegion() && current_survey_topic == 0) {
        this.fillAlpha = 255;
      } else {
        this.fillAlpha = 100;
      }

    }

    display() {


      textSize(font_pixels)
      textAlign(LEFT, CENTER)
      fill(255, this.fillAlpha);
      rect(this.x, this.y, this.w, this.h, 4, 4);
      fill(0, 255);
      text(this.button_name, this.text_x, this.text_y);
    }


  }
